import React, { Component } from 'react'

import Child from './Child'
export default class Parent extends Component {
    constructor (){
        super();
        this.state = {
            name:'刘亦菲',
            age:26,
            sonSay:'',
        }
    }
    getMessage(e){
        this.setState({sonSay:e});
    }
    render() {
        return (
            <div className='alert alert-info'>
                <h1>父组件</h1>
                <p>name为：{this.state.name}</p>
                <p>age为：{this.state.age}</p>
                <p>子组件讲：{this.state.sonSay}</p>
                <Child name={this.state.name} age={this.state.age} message={(e)=>{this.getMessage(e)}}></Child>
            </div>
        )
    }
}
