import React, { Component } from 'react'
import Child from './Child'
export default class Ref extends Component {
    constructor(){
        super();
        // 方式三：创建ref对象
        this.refAdd = React.createRef();
    }
    render() {
        return (
            <div className='alert alert-info'>
                {/* 方式一 */}
                {/* <div ref='div'>测试div</div> */}
                {/* <button className='btn btn-success' onClick={()=>this.change()}>更改样式</button> */}

                {/* 方式二 */}
                {/* <div ref={dom=>this.add=dom}>方式二测试</div>
                <button className='btn btn-success' onClick={()=>this.change1()}>二</button> */}

                {/* 方式三 */}
                {/* <div ref={this.refAdd}>方式三测试</div>
                <button className='btn btn-success' onClick={()=>this.change2()}>三</button> */}
                {/* 操作子元素 */}
                <Child ref={dom=>this.child=dom}></Child>
                <button className='btn btn-success' onClick={()=>this.getChild()}>三</button>
            </div>
        )
    }
    change(){
        // 用法一:操作元素内容
        this.refs.div.innerHTML = '<i>更改后的内容</i>';
        this.refs.div.style.color = 'yellow';
    };
    change1(){
        // 用法二，可以作为形参传递当前元素
        console.log(this.add);
    };
    change2(){
        // 用法二，可以作为形参传递当前元素
        console.log(this.refAdd);//{current: div}对象
        console.log(this.refAdd.current);//当前元素
    };
    getChild(){
        console.log(this.child);//子组件内容
        console.log(this.child.state);//子组件内容
        console.log(this.child.message);//子组件内容
    }
}
