import React, { Component } from 'react'

export default class Ref extends Component {
    render() {
        return (
            <div className='alert alert-info'>
                {/* 方式一 */}
                {/* <div ref='div'>测试div</div> */}
                {/* <button className='btn btn-success' onClick={()=>this.change()}>更改样式</button> */}

                {/* 方式二 */}
                {/* <div ref={dom=>this.add=dom}>方式二测试</div>
                <button className='btn btn-success' onClick={()=>this.change1()}>二</button> */}

                {/* 方式三 */}
                <div ref={this.refAdd=dom}>方式二测试</div>
                <button className='btn btn-success' onClick={()=>this.change2()}>三</button>
            </div>
        )
    }
    change(){
        // 用法一:操作元素内容
        this.refs.div.innerHTML = '<i>更改后的内容</i>';
        this.refs.div.style.color = 'yellow';
    };
    change1(){
        // 用法二，可以作为形参传递当前元素
        console.log(this.add);
    }
}
