import React, { Component } from 'react'

export default class Child extends Component {
    constructor (){
        super();
        this.state = {
            toParent:'想要零花钱'
        }
    }
    send(){
        this.props.message(this.state.toParent);
    }
    render() {
        return (
            <div>
                <h2>子组件</h2>
                <p>父组件的name值为：{this.props.name}</p>
                <p>父组件的age值为：{this.props.age}</p>
                <button onClick={()=>this.send()}>发送</button>
            </div>
        )
    }
}
